import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

import Create from './components/create.component';
import Edit from './components/edit.component';
import Index from './components/index.component';
import Login from './components/Loginpage'
import axios from 'axios'; 


class App extends Component {



    state = {
        title : '',
        body : '',
        posts : ''

    };

    componentDidMount = () => {
        this.getData(); 

         fetch('https://jsonplaceholder.typicode.com/users')
            .then(res => res.json())
            .then(json => {
                this.setState({
                    items: json,
                    isLoaded: true, 
                })
            }).catch((err) => {
                console.log(err);
            });

    }

    getData = () => {
        axios.get('http://localhost:4000/business')
        .then((response) => {
            const data = response.data; 
            this.setState({posts : data});
            console.log('data reveived'); 

        })
        .catch(() => {
            console.log('errorrrr');
        }  )

    }

    displayProfile = (posts) => {
        if (!posts.length){

            return <div> No Profile Found</div>; 
        }
        return posts.map((post, index) => (
            <div key = {index}>

                <h2> {post.person_name} </h2>
                <p> {post.business_name} </p>
                </div> 
        )


        );
    }; 


  render() {

    return (
      <Router>
        <div className="container">
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <Link to={'/'} className="navbar-brand">Profile Page</Link>
            <div className="collapse navbar-collapse" id="navbarSupportedContent">
              <ul className="navbar-nav mr-auto">

        <li className="nav-item">
                  <Link to={'/create'} className="nav-link">Admin</Link>
                </li>                

              </ul>
            </div>
          </nav> <br/>

          <div>{this.displayProfile(this.state.posts)}</div> <br/>


            <div>

        const { isLoaded, items } = this.state;

        if (!isLoaded)
            return <div>Loading...</div>;

        return (
            <div className="App">
                <ul>
                    {items.map(item => (
                        <li key={item.id}>
                            Name: {item.name} | Email: {item.email}
                        </li>
                    ))}
                </ul>
            </div>
        );

    }


            </div>




          <Switch>

              <Route exact path='/create' component={ Index } />
              <Route path='/edit/:id' component={ Edit } />

              <Route path='/index' component={ Index } />
          </Switch>
        </div>
      </Router>
    );
  }
}





export default App;
